function _initMidway(){"use strict";var e=document.querySelectorAll(".midway-horizontal"),t=document.querySelectorAll(".midway-vertical");for(var n=0;n<e.length;n++){e[n].style.marginLeft=-e[n].offsetWidth/2+"px";e[n].style.display="inline";e[n].style.position="absolute";e[n].style.left="50%"}for(var n=0;n<t.length;n++){t[n].style.marginTop=-t[n].offsetWidth/2+"px";t[n].style.display="inline";t[n].style.position="absolute";t[n].style.top="50%"}}window.onload=function(){_initMidway()};window.onresize=function(){_initMidway()}